import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
    CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend
);

function Analytics({ range, runs }) {

    const processRuns = (data, range) => {
        const endDate = new Date();
        let startDate;
        switch (range) {
            case '7D':
                startDate = new Date(endDate);
                startDate.setDate(startDate.getDate() - 7);
                break;
            case '1M':
                startDate = new Date(endDate);
                startDate.setMonth(startDate.getMonth() - 1);
                break;
            case '3M':
                startDate = new Date(endDate);
                startDate.setMonth(startDate.getMonth() - 3);
                break;
            case '6M':
                startDate = new Date(endDate);
                startDate.setMonth(startDate.getMonth() - 6);
                break;
            case '1Y':
                startDate = new Date(endDate);
                startDate.setFullYear(startDate.getFullYear() - 1);
                break;
            default:
                startDate = new Date(endDate);
                startDate.setDate(startDate.getDate() - 7);
        }

        const dateMap = {};
        data.forEach((run) => {
            const runDate = new Date(run.date).toDateString();
            dateMap[runDate] = run.distance;
        });

        const dates = [];
        for (
            let d = new Date(startDate);
            d <= endDate;
            d.setDate(d.getDate() + 1)
        ) {
            const dateStr = new Date(d).toDateString();
            dates.push({
                date: dateStr,
                distance: dateMap[dateStr] || 0,
            });
        }

        return dates;
    };

    const processedRuns = processRuns(runs, range);

    const chartData = {
        labels: processedRuns.map((run) => run.date),
        datasets: [
            {
                label: 'Distance (mi)',
                data: processedRuns.map((run) => run.distance),
                fill: true,
                backgroundColor: 'rgba(255,87,34,0.2)',
                borderColor:'#FF5722',
                pointBackgroundColor: '#FF5722',
            },
        ],
    };

    const chartOptions = {
        scales: {
            y: {
                beginAtZero: true,
                suggestedMax: 10,
            },
            x: {
                type: 'category',
                ticks: {
                    maxRotation: 45, // Rotate the labels at a 45-degree angle
                    minRotation: 45
                }
            }
        }
    };

    return (
        <div>
            <Line data={chartData} options={chartOptions} />
        </div>
    );
}

export default Analytics;
