// /frontend/src/components/DeleteRun.js

import React, { useState } from 'react';

function DeleteRun({ runs, onRunDeleted }) {
    const [selectedRunId, setSelectedRunId] = useState('');
    const [error, setError] = useState('');

    const handleDelete = async () => {
        error.preventDefault();
        if (!selectedRunId) {
            setError('Please select a run to delete.');
            return;
        }
        onRunDeleted(selectedRunId);
    };

    const handleRunSelect = (e) => {
        setSelectedRunId(e.target.value);
    };

    return (
        <div>
            <h2>Delete Run</h2>
            <select onChange={handleRunSelect} value={selectedRunId}>
                <option value="">Select a run to delete</option>
                {runs.map(run => (
                    <option key={run.id} value={run.id}>
                        {run.date} - {run.distance} mi - {run.time}
                    </option>
                ))}
            </select>
            <button onClick={handleDelete}>Delete Run</button>
            {error && <p>{error}</p>}
        </div>
    );
}

export default DeleteRun;
