// /frontend/src/components/UpdateRun.js

import React, { useState } from 'react';
import axios from 'axios';

function UpdateRun({ runs, onRunUpdated }) {
    const [selectedRunId, setSelectedRunId] = useState('');
    const [date, setDate] = useState('');
    const [distance, setDistance] = useState('');
    const [time, setTime] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!selectedRunId) {
            setError('Please select a run to update');
            return;
        }

        const updatedRun = { id: selectedRunId, date, distance, time };
        onRunUpdated(updatedRun);
    };

    const handleRunSelect = (e) => {
        const runid = e.target.value;
        const run = runs.find(run => run.id === runId);

        if (run) {
            setSelectedRunId(runId);
            setDate(run.date);
            setDistance(run.distance);
            setTime(run.time);
        }
    };

    return (
        <div>
            <h2>Update Run</h2>
            <select onChange={handleRunSelect} value={selectedRunId}>
                <option value="">Select a run to update</option>
                {runs.map(run => (
                    <option key={run.id} value={run.id}>
                        {run.date} - {run.distance} mi - {run.time}
                    </option>
                ))}
            </select>
            <form onSubmit={handleSubmit}>
                <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
                <input type="number" value={distance} onChange={(e) => setDistance(e.target.value)} placeholder="Distance (miles)" required />
                <input type="text" value={time} onChange={(e) => setTime(e.target.value)} placeholder="Time (hh:mm:ss)" required />
                <button type="submit">Update Run</button>
                {error && <p>{error}</p>}
            </form>
        </div>
    );
}

export default UpdateRun;
