// /frontend/src/components/Dashboard.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import AddRun from './AddRun';
import UpdateRun from './UpdateRun';
import DeleteRun from './DeleteRun';
import Analytics from './Analytics';

function Dashboard() {
    const navigate = useNavigate();

    useEffect(() => {
        fetchRuns();
    }, [range, runs]);

    const fetchRuns = async () => {
        try {
            const token = localStorage.getItem('token');
            const res = await fetch('http://localhost:5000/api/runs', {
                method: 'GET',
                headers: {
                    'x-auth-token': token
                }
            });
            const data = await res.json();
            setRuns(data);
        } catch (err) {
            console.error('Error fetching runs:', err);
        }
    };

    const handleRunAdded = (newRun) => {
        setRuns([...runs, newRun]);
    };

    const handleRunUpdated = (updatedRun) => {
        setRuns(runs.map(run => run.id === updatedRun.id ? updatedRun : run));
    };

    const handleRunDeleted = (runId) => {
        setRuns(runs.filter(run => run.id !== runId));
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    return (
        <div>
            <h2>Dashboard</h2>
            <button onClick={handleLogout}>Logout</button>
            <div>
                <button onClick={() => setRange('7D')}>7D</button>
                <button onClick={() => setRange('1M')}>1M</button>
                <button onClick={() => setRange('3M')}>3M</button>
                <button onClick={() => setRange('6M')}>6M</button>
                <button onClick={() => setRange('1Y')}>1Y</button>
            </div>
            <Analytics range={range} runs={runs} />
            <AddRun onRunAdded={handleRunAdded} />
            <UpdateRun onRunUpdated={handleRunUpdated} />
            <DeleteRun onRunDeleted={handleRunDeleted} />
            <div>
                <h3>Your Runs:</h3>
                <ul>
                    {runs.map(run => (
                        <li key={run.id}>
                            {run.date} - {run.distance} mi - {run.time} - {run.pace}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Dashboard;
