import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Analytics from './Analytics';

function Dashboard() {
    const [runs, setRuns] = useState([]);
    const [range, setRange] = useState('7D');
    const navigate = useNavigate();

    useEffect(() => {
        fetchRuns();
    }, []);

    const fetchRuns = async () => {
        try {
            const token = localStorage.getItem('token');
            const res = await fetch('http://localhost:5000/api/runs', {
                method: 'GET',
                headers: {
                    'x-auth-token': token
                }
            });
            const data = await res.json();
            setRuns(data);
        } catch (err) {
            console.error('Error fetching runs:', err);
        }
    };

    const handleRunAdded = async (newRun) => {
        try {
            const token = localStorage.getItem('token');
            const res = await axios.post('http://localhost:5000/api/runs/log', newRun, {
                headers: { 'x-auth-token': token }
            });
            setRuns([...runs, res.data]);
        } catch (err) {
            console.error('Error adding run:', err);
        }
    };

    const handleRunUpdated = async (runId, updatedRun) => {
        try {
            const token = localStorage.getItem('token');
            const res = await axios.put(`http://localhost:5000/api/runs/${runId}`, updatedRun, {
                headers: { 'x-auth-token': token }
            });
            setRuns(runs.map(run => run.id === runId ? res.data : run));
        } catch (err) {
            console.error('Error updating run:', err);
        }
    };

    const handleRunDeleted = async (runId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:5000/api/runs/${runId}`, {
                headers: { 'x-auth-token': token }
            });
            setRuns(runs.filter(run => run.id !== runId));
        } catch (err) {
            console.error('Error deleting run:', err);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    return (
        <div>
            <h2>Dashboard</h2>
            <button onClick={handleLogout}>Logout</button>
            <div>
                <button onClick={() => setRange('7D')}>7D</button>
                <button onClick={() => setRange('1M')}>1M</button>
                <button onClick={() => setRange('3M')}>3M</button>
                <button onClick={() => setRange('6M')}>6M</button>
                <button onClick={() => setRange('1Y')}>1Y</button>
            </div>
            <Analytics range={range} runs={runs} />
            <div>
                <h3>Your Runs:</h3>
                <ul>
                    {runs.map(run => (
                        <li key={run.id}>
                            {run.date} - {run.distance} mi - {run.time} - {run.pace}
                            <button onClick={() => handleRunUpdated(run.id, { date: run.date, distance: run.distance, time: run.time })}>Update</button>
                            <button onClick={() => handleRunDeleted(run.id)}>Delete</button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Dashboard;
