import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
    CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend
);

function FitnessScoreChart({ user }) { 
    const fitnessData = user.fitnessScoreHistory || [];
    console.log(fitnessData);

    useEffect(() => {
        fetchFitnessData();
    }, []);

    const fetchFitnessData = async () => {
        try {
            const token = localStorage.getItem('token');
            const res = await axios.get('http://localhost:5000/api/user/fitness-score', {
                headers: {
                    'x-auth-token': token
                }
            });

            setFitnessData(res.data || []);
        } catch (err) {
            console.error('Error fetching fitness data', err);
            setFitnessData([]);
        }
    };

    const chartData = {
        labels: fitnessData.map(data => data.date),
        datasets: [
            {
                label: 'Fitness Score',
                data: fitnessData.map(data => data.score),
                fill: true,
                backgroundColor: 'rgba(34,139,34,0.2)',
                borderColor: '#228822',
                pointBackgroundColor: '#228822',
            }
        ]
    };

    const chartOptions = {
        scales: {
            y: {
                beginAtZero: true,
                suggestedMax: 100,
            },
            x: {
                type: 'category',
                ticks: {
                    maxRotation: 45,
                    minRotation: 45
                }
            }
        }
    };

    return (
        <div>
            <h3>Your Fitness Score Over Time</h3>
            <Line data={chartData} options={chartOptions} />
        </div>
    );
}

export default FitnessScoreChart;
